import React, { Component } from 'react';
import { Button, Checkbox, Icon, Table } from 'semantic-ui-react'

class TableRow extends Component {

  state = {
    movie: {}
  }

  findMovie = () => {
    fetch(`https://api.themoviedb.org/3/movie/${this.queueObj.queue_id}?api_key=3d6fab529007c80701a5d4ed2a0df61e`)
      .then(r => r.json())
      .then(movieObj => {
        this.setState({
          movie: {...movieObj}
        })
      })
  }

  render() {
    console.log(this.props.queueObj);
    return (
      <Table.Body>
        <Table.Row>
          <Table.Cell collapsing>
            <Checkbox slider />
          </Table.Cell>
          <Table.Cell>{ this.props.queueObj.title }</Table.Cell>
          <Table.Cell>{ this.props.queueObj.vote_average }</Table.Cell>
          <Table.Cell>{ this.props.queueObj.release_date }</Table.Cell>
          <Table.Cell>{ this.props.queueObj.vote_average }</Table.Cell>
        </Table.Row>
      </Table.Body>
    );
  }

}

export default TableRow;
