import React from 'react';
import { Card, Icon, Image } from 'semantic-ui-react'

class MovieContainer extends React.Component {

  movieObj = this.props.movies[Math.floor(Math.random() * 19)]

  render() {
    return(
      <Card>
        <Image src={this.movieObj.poster_path} wrapped ui={false} />
        <Card.Content>
          <Card.Header>{this.movieObj.title}</Card.Header>
          <Card.Meta>{this.movieObj.popularity}</Card.Meta>
          <Card.Meta>{this.movieObj.release_date}</Card.Meta>
          <Card.Description>
            {this.movieObj.overview}
          </Card.Description>
        </Card.Content>
        <Card.Content extra>
          <a>
            <Icon name='angle double left' />
          </a>
        </Card.Content>
      </Card>
    )

  }


}

export default MovieContainer;
