import { NavLink } from 'react-router-dom'
import PropTypes from 'prop-types';
import React from 'react';
import {
  Icon,
  Menu,
  Sidebar,
} from 'semantic-ui-react';

const VerticalSidebar = ({ animation, direction, visible, handleShowQueue }) => {

  let handleClick = () => {
    console.log("hi");
  }

  <Sidebar
    as={Menu}
    animation={animation}
    direction={direction}
    icon='labeled'
    inverted
    vertical
    visible={visible}
    width='thin'
  >
    <NavLink to='/' exact activeClassName="selected">
      <Menu.Item>
        <Icon name='home' />
        Home
      </Menu.Item>
    </NavLink>
    <div onClick={ handleClick } className="queue-button">
      <Menu.Item>
        <Icon name='film' />
        Queue
      </Menu.Item>
    </div>
    <NavLink to='/login' exact activeClassName="selected">
      <Menu.Item>
        <Icon name='address card' />
        Login
      </Menu.Item>
    </NavLink>
    <NavLink to='/register' exact activeClassName="selected">
      <Menu.Item>
        <Icon name='plus' />
          Register
      </Menu.Item>
    </NavLink>
  </Sidebar>
}

VerticalSidebar.propTypes = {
  animation: PropTypes.string,
  direction: PropTypes.string,
  visible: PropTypes.bool,
}


export default VerticalSidebar;
